          <ModalContent >
            <ModalHeader>Header</ModalHeader>
            <Divider/>
            <ModalBody>
              <Box>
                <Heading>Description</Heading>
                <Text>Lorem ipsum.</Text>
              </Box>
              <Divider/>
              <Box>
                <Heading>Price</Heading>
                <Text>Price</Text>
              </Box>
              <Divider/>
              <Box>
                <Heading>Example</Heading>
                <Text>Location</Text>
              </Box>
            </ModalBody>
            <ModalFooter>
              <Button colorScheme="blue" onClick={handleCloseModal}>
                Close
              </Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      )}
    </Box>
  );
};

export default OnlineUserList;
