import React from "react";
import { Spinner, ModalOverlay, Center } from "@chakra-ui/react";

const Loader = () => {
  return (
    <ModalOverlay>
      <Center>
        <Spinner
          thickness="4px"
          speed="0.65s"
          emptyColor="gray.200"
          color="blue.500"
          size="xl"
        />
      </Center>
    </ModalOverlay>
  );
};

export default Loader;
