import React, { useState } from "react";
import axios from "axios";
import localStorage from "local-storage";
import { useNavigate } from "react-router-dom";
import loginIllustration from "../../Images/login-illustration.png";
import Footer from "../../Components/footer/Footer";
import logo from "../../Images/logo_transparent.png";
import Loader from "../../Components/Loader/Loader";
import { Fade } from "@chakra-ui/react";
import PopupError from "../../Components/PopupError/PopupError";

import {
  Box,
  Button,
  Checkbox,
  Container,
  Divider,
  FormControl,
  FormLabel,
  Heading,
  HStack,
  Input,
  Stack,
  Text,
} from '@chakra-ui/react'

import "./LoginStyle.css";



const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [showErrorPopup, setShowErrorPopup] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const navigate = useNavigate();

  const handleCloseErrorPopup = () => {
    setShowErrorPopup(false);
  };

  const handleLogin = (event) => {
    event.preventDefault();

    setLoading(true);

    fetch("https://localhost:50555/api/UserAccount/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Authentication failed!");
        }
        return response.json();
      })
      .then(() => {
        navigate("/admin");
      })
      .catch((error) => {
        setLoading(false);
        setShowErrorPopup(true);
        console.error("Login failed", error);
      });
  };

  return (
    <div className="login-container">
      <header className="login-header">
        <div className="login-header-content">
          <img src={logo} className="login-logo" />
          <h1 className="login-title">AdReach</h1>
        </div>
      </header>
      <div className="login-form-container">
        <h2 className="login-form-title">
          Set your advertisements to the next level
        </h2>
        <p className="login-form-text">
          Lorem ipsum dolor sit amet, consectetur adipisci elit, sed eiusmod
          tempor incidunt ut labore et dolore magna aliqua.
        </p>
        <img className="login-illustration" src={loginIllustration} />
        <form className="login-form">
          <div className="login-textfields">
            <input
              onChange={(e) => setUsername(e.target.value)}
              className="username-textfield login-textfield"
              placeholder="E-mail or username"
            />
            <input
              onChange={(e) => setPassword(e.target.value)}
              className="password-textfield login-textfield"
              type="password"
              placeholder="Password"
            />
          </div>
          <button
            type="submit"
            className="login-button login-submit-button"
            onClick={handleLogin}
          >
            Sign In
          </button>
        </form>
        <button
          className="login-button sign-up-button"
          onClick={() => navigate("/registration")}
        >
          Sign Up
        </button>
        {loading && <Loader />}
        <Fade initialScale={0.9} in={showErrorPopup}>
          <PopupError show={showErrorPopup} onClose={handleCloseErrorPopup} />
        </Fade>
      </div>
      <Footer />
    </div>
  );
};

export default Login;
