import React from "react";
import { Box, Heading, Grid, Button, Avatar, Flex } from "@chakra-ui/react";

const User = (props) => {
  const blocks = [1, 2, 3, 4, 5, 6, 7, 8, 9];
  return (
    <Box
      bg="gray.800"
      width="22em"
      height="100px"
      borderRadius="md"
      p={4}
      display="flex"
      alignItems="center"
      justifyContent="space-between"
    >
      <Flex alignItems="center">
        <Avatar name={props.name} src={props.avatarUrl} size="md" />
        <Heading size="xs" ml={4}>
          {props.name}
        </Heading>
      </Flex>
      <Button colorScheme="blue">Follow</Button>
    </Box>
  );
};

export default User;
