import React, { useState } from "react";
import { Box, Heading, Grid, Modal, ModalOverlay, ModalContent, ModalHeader } from "@chakra-ui/react";
import Advertisement from "../../../Components/Advertisement/Advertisement";

const AdvertisementList = () => {
  const blocks = [1, 2, 3, 4, 5, 6, 7, 8, 9];
  const [selectedAd, setSelectedAd] = useState(null);

  const handleOpenModal = (ad) => {
    setSelectedAd(ad);
  };

  const handleCloseModal = () => {
    setSelectedAd(null);
  };

  return (
    <Box p="2em">
      <Box>
        <Heading mb="1em">Advertisements</Heading>
        <Grid templateColumns="repeat(3, 1fr)" gap={4}>
          {blocks.map((block) => (
            <Advertisement
              key={block}
              ad={block}
              onOpenModal={handleOpenModal}
            />
          ))}
        </Grid>
      </Box>
      {selectedAd && (
        <Modal isOpen={selectedAd !== null} onClose={handleCloseModal}>
          <ModalOverlay/>
          <ModalContent>
            <ModalHeader>{selectedAd.title}</ModalHeader>
            <ModalBody>
              <p></p>
            </ModalBody>
          </ModalContent>
        </Modal>
      )}
    </Box>
  );
};

export default AdvertisementList;
