import React from "react";
import {
  Box,
  Heading,
  Table,
  Thead,
  Tbody,
  Tfoot,
  Tr,
  Th,
  Td,
  TableCaption,
  TableContainer,
  Button,
  Center,
} from "@chakra-ui/react";
import ActionLog from "./ActionLog";

const ActionLogList = ({ recentActions }) => {
  return (
    <Box p="2em">
      <Heading>Recent Actions</Heading>
      <TableContainer p="1em" bgColor="gray.200" mt="2em" borderRadius="1em">
        <Table variant="simple">
          <TableCaption>Imperial to metric conversion factors</TableCaption>
          <Thead>
            <Tr>
              <Th>Action</Th>
              <Th>User</Th>
              <Th>Date</Th>
            </Tr>
          </Thead>
          {recentActions.length > 0 ? (
            <Tbody>
              {recentActions.map((action) => (
                <ActionLog
                
                name={action.name}
                />
              ))}
            </Tbody>
          ) : (
            <Tbody>
              <Td
                borderTopLeftRadius="10px"
                borderBottomLeftRadius="10px"
                backgroundColor="gray.300"
              >
                No data
              </Td>
              <Td backgroundColor="gray.300" />
              <Td backgroundColor="gray.300" />
            </Tbody>
          )}
        </Table>
      </TableContainer>
      {recentActions.length > 0 ? null : (
        <Center>
          <Button colorScheme="blue" mt="2em">Refresh</Button>
        </Center>
      )}
    </Box>
  );
};

export default ActionLogList;
