import React from "react";
import { Spinner, Center, Modal, ModalOverlay } from "@chakra-ui/react";

const Loader = () => {
  return (
    <Modal>
      <ModalOverlay>
        <Center>
          <Spinner
            thickness="4px"
            speed="0.65s"
            emptyColor="gray.200"
            color="blue.500"
            size="xl"
          />
        </Center>
      </ModalOverlay>
    </Modal>
  );
};

export default Loader;
