import React from "react";
import { Box, Heading, Grid, Button, Avatar } from "@chakra-ui/react";

const User = (props) => {
  const blocks = [1, 2, 3, 4, 5, 6, 7, 8, 9];
  return (
    <Box
      bg="gray.800"
      width="22em"
      height="100px"
      borderRadius="md"
      p={4}
      display="flex"
      flexDirection="column"
      alignItems="center"
      justifyContent="space-between"
    >
      <Heading size="xs" mb={2}>{props.name}</Heading>
      <Avatar name={props.name} src={props.avatarUrl} size="md" />
      <Button colorScheme="blue">Follow</Button>
    </Box>
  );
};

export default User;
