import React, { useState } from "react";
import {
  Box,
  Heading,
  Grid,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Button,
  Text,
  Divider,
  Center,
} from "@chakra-ui/react";
import Advertisement from "../Advertisement/Advertisement";
import Task from "./Task";

const TaskList = ({ tasks }) => {
  const [selectedTask, setSelectedTask] = useState(null);

  const handleOpenModal = (inputTask) => {
    setSelectedTask(inputTask);
  };

  const handleCloseModal = () => {
    setSelectedTask(null);
  };

  return (
    <Box p="2em">
      <Box>
        <Heading mb="1em">Task List</Heading>
        <Task />
        {/*tasks.length > 0 ? (
          <Grid templateColumns="repeat(3, 1fr)" gap={4}>
            {tasks.map((block) => (
              <Advertisement />
            ))}
          </Grid>
        ) : (
          <Box textAlign="center" mt="2em">
            <Heading>No data</Heading>
          </Box>
        )*/}
      </Box>

      {tasks.length > 0 ? (
        <Center>
          <Button mt="2em" colorScheme="blue">
            Show all tasks
          </Button>
        </Center>
      ) : (
        <Center>
          <Button mt="2em" colorScheme="blue">
            Refresh
          </Button>
        </Center>
      )}

      {selectedTask && (
        <Modal isOpen={selectedAd !== null} onClose={handleCloseModal}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Header</ModalHeader>
            <Divider />
            <ModalBody>
              <Box>
                <Heading>Description</Heading>
                <Text>Lorem ipsum.</Text>
              </Box>
              <Divider />
              <Box>
                <Heading>Price</Heading>
                <Text>Price</Text>
              </Box>
              <Divider />
              <Box>
                <Heading>Example</Heading>
                <Text>Location</Text>
              </Box>
            </ModalBody>
            <ModalFooter>
              <Button mr={2} colorScheme="yellow" onClick={handleCloseModal}>
                Edit
              </Button>
              <Button mr={2} colorScheme="red" onClick={handleCloseModal}>
                Delete
              </Button>
              <Button mr={2} colorScheme="blue" onClick={handleCloseModal}>
                Close
              </Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      )}
    </Box>
  );
};

export default TaskList;
