import {
  Box,
  Flex,
  Avatar,
  Link,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  MenuDivider,
  useDisclosure,
  useColorModeValue,
  useColorMode,
  IconButton,
  CloseButton,
  Icon,
  Drawer,
  DrawerContent,
  Text,
  HStack,
  VStack,
  Heading,
  Button,
  Divider,
} from "@chakra-ui/react";

import {
  FiHome,
  FiTrendingUp,
  FiCompass,
  FiStar,
  FiSettings,
  FiMenu,
  FiBell,
  FiChevronDown,
  FiMessageCircle,
  FiDollarSign,
  FiUserCheck,
  FiUsers,
  FiSmile,
  FiMoon,
  FiSun,
  FiHeart,
} from "react-icons/fi";

import { IconType } from "react-icons";
import { ReactText } from "react";

import { MoonIcon, SunIcon } from "@chakra-ui/icons";
import Footer from "../footer/Footer";

const LinkItems = [
  { name: "Home", icon: FiHome },
  { name: "Analytics", icon: FiTrendingUp },
  { name: "Manage Users", icon: FiUsers },
  { name: "Manage Budget", icon: FiDollarSign },
  { name: "Compliances", icon: FiUserCheck },
  { name: "Command Chat", icon: FiMessageCircle },
  { name: "Customer Support", icon: FiSmile },
];

const Notifications = [
  { user: "User", date: "01.01.1980 at 12:00 AM", text: "Lorem ipsum" },
  { user: "User 1", date: "01.01.1980 at 12:00 AM", text: "Lorem ipsum" },
  { user: "User 2", date: "01.01.1980 at 12:00 AM", text: "Lorem ipsum" },
  { user: "User 3", date: "01.01.1980 at 12:00 AM", text: "Lorem ipsum" },
];

const NavLink = ({ children }) => (
  <Link
    px={2}
    py={1}
    rounded={"md"}
    _hover={{
      textDecoration: "none",
      bg: useColorModeValue("gray.200", "gray.700"),
    }}
    href={"#"}
  >
    {children}
  </Link>
);

export default function SidebarWithHeader({ children }) {
  const { colorMode, toggleColorMode } = useColorMode();
  const { isOpen, onOpen, onClose } = useDisclosure();
  return (
    <>
      <Box minH="100vh" bg={useColorModeValue("gray.100", "gray.900")}>
        <SidebarContent
          onClose={() => onClose}
          display={{ base: "none", md: "block" }}
        />
        <Drawer
          autoFocus={false}
          isOpen={isOpen}
          placement="left"
          onClose={onClose}
          returnFocusOnClose={false}
          onOverlayClick={onClose}
          size="full"
        >
          <DrawerContent>
            <SidebarContent onClose={onClose} />
          </DrawerContent>
        </Drawer>
        {/* mobilenav */}
        <MobileNav onOpen={onOpen} />
        <Box ml={{ base: 0, md: 60 }} p="4">
          {children}
        </Box>
      </Box>
    </>
  );
}

const SidebarContent = ({ onClose, ...rest }) => {
  return (
    <Box
      bg={useColorModeValue("white", "gray.900")}
      borderRight="1px"
      borderRightColor={useColorModeValue("gray.200", "gray.700")}
      w={{ base: "full", md: 60 }}
      pos="fixed"
      h="full"
      {...rest}
    >
      <Flex h="20" alignItems="center" mx="8" justifyContent="space-between">
        <Text fontSize="2xl" fontFamily="monospace" fontWeight="bold">
          Logo
        </Text>
        <CloseButton display={{ base: "flex", md: "none" }} onClick={onClose} />
      </Flex>
      {LinkItems.map((link) => (
        <NavItem key={link.name} icon={link.icon}>
          {link.name}
        </NavItem>
      ))}
    </Box>
  );
};

const NavItem = ({ icon, children, ...rest }) => {
  return (
    <Link
      href="#"
      style={{ textDecoration: "none" }}
      _focus={{ boxShadow: "none" }}
    >
      <Flex
        align="center"
        p="4"
        mx="4"
        borderRadius="lg"
        role="group"
        cursor="pointer"
        _hover={{
          bg: "cyan.400",
          color: "white",
        }}
        {...rest}
      >
        {icon && (
          <Icon
            mr="4"
            fontSize="16"
            _groupHover={{
              color: "white",
            }}
            as={icon}
          />
        )}
        {children}
      </Flex>
    </Link>
  );
};

