import { Box, Text } from "@chakra-ui/react";
import { useState } from "react";
import React from "react";

const StartPageIntro = () => {
  const [stats, setStats] = useState({
    users: 1000,
    products: 6052,
    revenue: 785,
  });
  return (
    <Box display="flex" justifyContent="space-around" alignItems="center" mt={10}>
      <Box textAlign="center">
        <Text fontSize="2xl" fontWeight="bold" mb={2}>Users</Text>
        <Text fontSize="4xl" fontWeight="bold" mb={2}>{stats.users}</Text>
      </Box>
      <Box textAlign="center">
        <Text fontSize="2xl" fontWeight="bold" mb={2}>Products</Text>
        <Text fontSize="4xl" fontWeight="bold" mb={2}>{stats.products}</Text>
      </Box>
      <Box textAlign="center">
        <Text fontSize="2xl" fontWeight="bold" mb={2}>Revenue</Text>
        <Text fontSize="4xl" fontWeight="bold" mb={2}>{stats.revenue}</Text>
      </Box>
    </Box>
  );
};

export default StartPageIntro;
