import React from "react";

const Instruction = () => {
    return (
        <Box mt={10}>
      <Flex align="center" justify="space-between">
        <Step number={1} text="Create your account" />
        <Step number={2} text="Choose your matching channels" />
        <Step number={3} text="Replenish the balance" />
        <Step number={4} text="Create a post with a picture and a link" />
        <Step number={5} text="Channel owner publishes a post and sends a link for verification" />
        <Step number={6} text="You can download the placement report" />
      </Flex>
    </Box>
    );
}

export default Instruction;
