import { ReactNode } from "react";
import {
  Box,
  Flex,
  Avatar,
  Link,
  Button,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  MenuDivider,
  useDisclosure,
  useColorModeValue,
  Stack,
  useColorMode,
  Center,
  VStack,
  Spacer,
  IconButton,
  CloseButton,
  Icon,
  Drawer,
  DrawerContent,
  Text,
  BoxProps,
  FlexProps,
  List,
  ListItem,
  Card,
  CardBody,
  Divider,
} from "@chakra-ui/react";

import {
  FiHome,
  FiTrendingUp,
  FiCompass,
  FiStar,
  FiSettings,
  FiMenu,
  FiBell,
  FiChevronDown,
} from "react-icons/fi";

import { IconType } from "react-icons";
import { ReactText } from "react";

import { MoonIcon, SunIcon } from "@chakra-ui/icons";
import Footer from "../footer/Footer";

const LinkItems = [
  { name: "Home", icon: FiHome },
  { name: "Trending", icon: FiTrendingUp },
  { name: "Explore", icon: FiCompass },
  { name: "Favourites", icon: FiStar },
  { name: "Settings", icon: FiSettings },
];

const items = [
  {
    id: 1,
    name: "Item 1",
    description: "Description 1",
    category: "Category 1",
  },
  {
    id: 2,
    name: "Item 2",
    description: "Description 2",
    category: "Category 2",
  },
  {
    id: 3,
    name: "Item 3",
    description: "Description 3",
    category: "Category 3",
  },
  {
    id: 4,
    name: "Item 3",
    description: "Description 3",
    category: "Category 3",
  },
  {
    id: 5,
    name: "Item 3",
    description: "Description 3",
    category: "Category 3",
  },
  {
    id: 6,
    name: "Item 3",
    description: "Description 3",
    category: "Category 3",
  },
  {
    id: 7,
    name: "Item 3",
    description: "Description 3",
    category: "Category 3",
  },
];

const NavLink = ({ children }) => (
  <Link
    px={2}
    py={1}
    rounded={"md"}
    _hover={{
      textDecoration: "none",
      bg: useColorModeValue("gray.200", "gray.700"),
    }}
    href={"#"}
  >
    {children}
  </Link>
);

export default function SidebarWithHeader({ children }) {
  const { colorMode, toggleColorMode } = useColorMode();
  const { isOpen, onOpen, onClose } = useDisclosure();
  return (
    <>
      <Box minH="100vh" bg={useColorModeValue('gray.100', 'gray.900')}>
      <SidebarContent
        onClose={() => onClose}
        display={{ base: 'none', md: 'block' }}
      />
      <Drawer
        autoFocus={false}
        isOpen={isOpen}
        placement="left"
        onClose={onClose}
        returnFocusOnClose={false}
        onOverlayClick={onClose}
        size="full">
        <DrawerContent>
          <SidebarContent onClose={onClose} />
        </DrawerContent>
      </Drawer>
      {/* mobilenav */}
      <MobileNav onOpen={onOpen} />
      <Box ml={{ base: 0, md: 60 }} p="4">
        {children}
      </Box>
    </Box>


      <Flex>
        <Box minH="100vh" bg={useColorModeValue("gray.100", "gray.900")}>
          <SidebarContent
            onClose={() => onClose}
            display={{ base: "none", md: "block" }}
          />
          <Drawer
            autoFocus={false}
            isOpen={isOpen}
            placement="left"
            onClose={onClose}
            returnFocusOnClose={false}
            onOverlayClick={onClose}
            size="full"
          >
            <DrawerContent>
              <SidebarContent onClose={onClose} />
            </DrawerContent>
          </Drawer>
          {/* mobilenav */}
          <MobileNav display={{ base: "flex", md: "none" }} onOpen={onOpen} />
          <Box ml={{ base: 0, md: 60 }} p="4">
            {children}
          </Box>
        </Box>

        {/* Place main content here */}
        <Box p="3em">
          <List spacing={3}>
            {items.map((item) => (
              <ListItem key={item.id} display="flex" alignItems="center">
                <Card>
                  <CardBody>
                    <Box display="flex" alignItems="center" marginRight="4">
                      <Box marginRight="4">
                        <Box
                          display="flex"
                          flexDirection="column"
                          alignItems="center"
                        >
                          <Box>
                            <Icon />
                          </Box>
                          <Divider orientation="vertical" />
                          <Box>
                            <Icon />
                          </Box>
                        </Box>
                      </Box>
                      <Box>
                        <Text>{item.name}</Text>
                        <Divider
                          marginTop="2"
                          marginBottom="2"
                          orientation="vertical"
                        />
                        <Box>
                          <Text>{item.description}</Text>
                          <Text>{item.category}</Text>
                        </Box>
                      </Box>
                      <Divider orientation="vertical" />
                      <Box ml={10}>
                        <Text>Example 1</Text>
                        <Text>Example 2</Text>
                        <Text>Example 3</Text>
                      </Box>
                      <Box ml={10}>
                        <Text>Example 1</Text>
                        <Text>Example 2</Text>
                        <Text>Example 3</Text>
                      </Box>
                      <Box ml={10}>
                        <Text>Example 1</Text>
                        <Text>Example 2</Text>
                        <Text>Example 3</Text>
                      </Box>
                      <Box ml={10}>
                        <Text>Example 1</Text>
                        <Text>Example 2</Text>
                        <Text>Example 3</Text>
                      </Box>
                      <Box ml={10}>
                        <Text>Example 1</Text>
                        <Text>Example 2</Text>
                        <Text>Example 3</Text>
                      </Box>
                    </Box>
                  </CardBody>
                </Card>
              </ListItem>
            ))}
          </List>
        </Box>
      </Flex>
      <Footer />
    </>
  );
}

const SidebarContent = ({ onClose, ...rest }) => {
  return (
    <Box
      bg={useColorModeValue("white", "gray.900")}
      borderRight="1px"
      borderRightColor={useColorModeValue("gray.200", "gray.700")}
      w={{ base: "full", md: 60 }}
      pos="fixed"
      h="full"
      {...rest}
    >
      <Flex h="20" alignItems="center" mx="8" justifyContent="space-between">
        <Text fontSize="2xl" fontFamily="monospace" fontWeight="bold">
          Logo
        </Text>
        <CloseButton display={{ base: "flex", md: "none" }} onClick={onClose} />
      </Flex>
      {LinkItems.map((link) => (
        <NavItem key={link.name} icon={link.icon}>
          {link.name}
        </NavItem>
      ))}
    </Box>
  );
};

const NavItem = ({ icon, children, ...rest }) => {
  return (
    <Link
      href="#"
      style={{ textDecoration: "none" }}
      _focus={{ boxShadow: "none" }}
    >
      <Flex
        align="center"
        p="4"
        mx="4"
        borderRadius="lg"
        role="group"
        cursor="pointer"
        _hover={{
          bg: "cyan.400",
          color: "white",
        }}
        {...rest}
      >
        {icon && (
          <Icon
            mr="4"
            fontSize="16"
            _groupHover={{
              color: "white",
            }}
            as={icon}
          />
        )}
        {children}
      </Flex>
    </Link>
  );
};

const MobileNav = ({ onOpen, ...rest }) => {
  return (
    <Flex
      ml={{ base: 0, md: 60 }}
      px={{ base: 4, md: 24 }}
      height="20"
      alignItems="center"
      bg={useColorModeValue("white", "gray.900")}
      borderBottomWidth="1px"
      borderBottomColor={useColorModeValue("gray.200", "gray.700")}
      justifyContent="flex-start"
      {...rest}
    >
      <IconButton
        variant="outline"
        onClick={onOpen}
        aria-label="open menu"
        icon={<FiMenu />}
      />

      <Text fontSize="2xl" ml="8" fontFamily="monospace" fontWeight="bold">
        Logo
      </Text>
    </Flex>
  );
};
