import React, { useState } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  Avatar,
  Box,
  Flex,
  IconButton,
  Text,
  Heading,
  Image,
  Button,
  Menu,
  MenuB
} from "@chakra-ui/react";

import { BiLike, BiChat, BiShare } from "react-icons/bi";

const Post = ({ publication }) => {
  const [likeOptionsVisible, setLikeOptionsVisible] = useState(false);

  const handleLikeButtonHold = () => {
    setLikeOptionsVisible(true);
  };

  const handleLikeButtonRelease = () => {
    setLikeOptionsVisible(false);
  };
  return (
    <Card maxW="xl" mt="3em">
      <CardHeader>
        <Flex spacing="4">
          <Flex flex="1" gap="4" alignItems="center" flexWrap="wrap">
            <Avatar name="Segun Adebayo" src="https://bit.ly/sage-adebayo" />

            <Box>
              <Heading size="sm">{}</Heading>
              <Text>Creator, Chakra UI</Text>
            </Box>
          </Flex>
          <IconButton
            variant="ghost"
            colorScheme="gray"
            aria-label="See menu"
          />
          {/*
          import { BsThreeDotsVertical } from "react-bootstrap";
          icon={<BsThreeDotsVertical />} 
          */}
        </Flex>
      </CardHeader>
      <CardBody>
        <Text>
          With Chakra UI, I wanted to sync the speed of development with the
          speed of design. I wanted the developer to be just as excited as the
          designer to create a screen.
        </Text>
      </CardBody>
      <Image
        objectFit="cover"
        src="https://images.unsplash.com/photo-1531403009284-440f080d1e12?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1770&q=80"
        alt="Chakra UI"
      />

      <CardFooter
        justify="space-between"
        flexWrap="wrap"
        sx={{
          "& > button": {
            minW: "136px",
          },
        }}
      >
        <Button
          flex="1"
          variant="ghost"
          leftIcon={<BiLike />}
          onMouseDown={handleLikeButtonHold}
          onMouseUp={handleLikeButtonRelease}
          onMouseLeave={handleLikeButtonRelease}
        >
          Like
        </Button>
        <Button flex="1" variant="ghost" leftIcon={<BiChat />}>
          Comment
        </Button>
        <Button flex="1" variant="ghost" leftIcon={<BiShare />}>
          Share
        </Button>
        {likeOptionsVisible && (
          <Menu>
            <MenuButton as={Button} variant="ghost" leftIcon={<BiLike />} size="sm">
              Like Options
            </MenuButton>
            <MenuList>
              <MenuItem>Like with Reaction 1</MenuItem>
              <MenuItem>Like with Reaction 2</MenuItem>
              <MenuItem>Like with Reaction 3</MenuItem>
              {/* Add more menu items for different like options */}
            </MenuList>
          </Menu>
        )}
      </CardFooter>
    </Card>
  );
};

export default Post;
