import { Box } from "@chakra-ui/react";
import { useState } from "react";
import React from "react";

const StartPageIntro = () => {
  const [stats, setStats] = useState({
    users: 0,
    products: 0,
    revenue: 0,
  });
  return (
  <Box display="flex" justifyContent="space-around" mt={10}>
  <Box textAlign="center">
    <Text fontSize="4xl" fontWeight="bold" mb={2}>
      {stats.users}
    </Text>
    <Text fontSize="lg" color="gray.500">Users</Text>
  </Box>
  <Box textAlign="center">
    <Text fontSize="4xl" fontWeight="bold" mb={2}>
      {stats.products}
    </Text>
    <Text fontSize="lg" color="gray.500">Products</Text>
  </Box>
  <Box textAlign="center">
    <Text fontSize="4xl" fontWeight="bold" mb={2}>
      {stats.revenue}
    </Text>
    <Text fontSize="lg" color="gray.500">Revenue</Text>
  </Box>
</Box>)
};

export default StartPageIntro;
