import { Box, Text, Avatar, Heading, Card } from "@chakra-ui/react";
import { useState } from "react";
import React from "react";

const StartPageIntro = () => {
  const [stats, setStats] = useState({
    users: 1000,
    products: 6052,
    revenue: 785,
  });
  return (
    <>
      <Heading mt={10} textAlign="center">
        Placing advertisements in any channel in any messenger and social media
      </Heading>
      <Box display="flex" justifyContent="space-around" mt={10}>
        <Card textAlign="center" padding={20} borderRadius={10}>
          <Text fontSize="4xl" fontWeight="bold" mb={2}>
            {stats.users}
          </Text>
          <Text fontSize="lg" color="gray.500">
            Users
          </Text>
        </Card>
        <Card textAlign="center" padding={20} borderRadius={10}>
          <Text fontSize="4xl" fontWeight="bold" mb={2}>
            {stats.products}
          </Text>
          <Text fontSize="lg" color="gray.500">
            Products
          </Text>
        </Card>
        <Card textAlign="center" padding={20} borderRadius={10}>
          <Text fontSize="4xl" fontWeight="bold" mb={2}>
            {stats.revenue}
          </Text>
          <Text fontSize="lg" color="gray.500">
            Revenue
          </Text>
        </Card>
      </Box>
    </>
  );
};

export default StartPageIntro;
