import React from "react";
import {
  Box,
  Flex,
  Text,
  IconButton,
  useDisclosure,
  Slide,
  SlideFade,
} from "@chakra-ui/react";
import { ChevronLeftIcon, ChevronRightIcon } from "@chakra-ui/icons";

const images = [
  {
    id: 1,
    src: "https://via.placeholder.com/600x300/1abc9c/ffffff",
    alt: "Carousel image 1",
    title: "Carousel image 1",
  },
  {
    id: 2,
    src: "https://via.placeholder.com/600x300/3498db/ffffff",
    alt: "Carousel image 2",
    title: "Carousel image 2",
  },
  {
    id: 3,
    src: "https://via.placeholder.com/600x300/9b59b6/ffffff",
    alt: "Carousel image 3",
    title: "Carousel image 3",
  },
  {
    id: 4,
    src: "https://via.placeholder.com/600x300/f1c40f/ffffff",
    alt: "Carousel image 4",
    title: "Carousel image 4",
  },
];

const FeedbackCarousel = () => {
  const { isOpen, onToggle } = useDisclosure();
  const [currentImageIndex, setCurrentImageIndex] = React.useState(0);

  const handleNextImage = () => {
    const lastIndex = images.length - 1;
    const shouldResetIndex = currentImageIndex === lastIndex;
    const index = shouldResetIndex ? 0 : currentImageIndex + 1;

    setCurrentImageIndex(index);
  };

  const handlePrevImage = () => {
    const lastIndex = images.length - 1;
    const shouldResetIndex = currentImageIndex === 0;
    const index = shouldResetIndex ? lastIndex : currentImageIndex - 1;

    setCurrentImageIndex(index);
  };

  const slideStyles = {
    position: "relative",
    top: "0",
    left: "0",
    width: "100%",
    height: "100%",
  };

  return (
    <>
    <Box position="relative" maxW="600px" margin="0 auto">
      {images.map((image, index) => (
        <SlideFade
          key={image.id}
          in={currentImageIndex === index}
          style={slideStyles}
        >
          <img src={image.src} alt={image.alt} style={{ width: "100%" }} />
          <Box
            position="relative"
            bottom="0"
            left="0"
            right="0"
            p="4"
            bg="rgba(0,0,0,0.4)"
            color="white"
            display={isOpen ? "block" : "none"}
          >
            <Text fontSize="xl" fontWeight="bold">
              {image.title}
            </Text>
            <Text fontSize="sm" mt="2">
              {image.alt}
            </Text>
          </Box>
        </SlideFade>
      ))}

      <Flex position="relative" top="50%" left="4" zIndex="1">
        <IconButton
          aria-label="Previous Image"
          icon={<ChevronLeftIcon />}
          onClick={handlePrevImage}
          mr="2"
        />
        <IconButton
          aria-label="Next Image"
          icon={<ChevronRightIcon />}
          onClick={handleNextImage}
        />
      </Flex>

      <Box
        position="relative"
        bottom="0"
        right="0"
        p="4"
        bg="rgba(0,0,0,0.4)"
        color="white"
      >
        <Text fontSize="xl" fontWeight="bold">
          {images[currentImageIndex].title}
        </Text>
        <Text fontSize="sm" mt="2">
          {images[currentImageIndex].alt}
        </Text>
        <IconButton
          aria-label="Toggle caption"
          icon={isOpen ? "chevron-up" : "chevron-down"}
          onClick={onToggle}
          variant="ghost"
          size="sm"
          mt="2"
        />
      </Box>
    </Box>
    </>
  );
};

export default FeedbackCarousel;
